function useStats(){
    const[stats, setStats] = useState();
    useEffect(() => {
        async function fetchData() {
            console.log('Fetching Data');
            const data = await fetch('http://covid19.mathdro.id/api/').then
            (res => res.json());
            setStats(data);
        }
        fetchData();       
    }, []);
    return stats;
}

export default useStats;
