import { useState } from 'react';
import useStats from "../utils/useStats.js"
import CountryDetails from "./CountryDetails"

function CountrySelector() {
    const countries = useStats('https://covid19.mathdro.id/api/countries');
    const [selectedCountry, setSelectedCountry] = useState('PRT');
    return (
    <div>
        <h1>{selectedCountry}</h1>
       <select
        onChange={e => {
            setSelectedCountry(e.target.value);
        }}>

       {countries.countries.map(function(country){
           return(
            <option
                selected = {selectedCountry === country.iso3}
                key={country.iso3}
                value={country.iso3}
            >
                {country.name}
            </option>
           )
        })}
       </select>
       <CountryDetails country = {selectedCountry}></CountryDetails>
    </div>


    )
}

export default CountrySelector
