import useStats from "../utils/useStats.js"

async function CountrySelector() {
    const countries = await useStats('https://covid19.mathdro.id/api/countries').then
    console.log(countries.countries)
    if(!countries) return <p>Loading...</p>
    return (
    <div>
       <select>
       {countries.countries.map((country) => {
          <option
            key={country.iso2}
            value={country.iso2}
          >
            {country.name}
          </option>
        })}
       </select>
    </div>
    )
}

export default CountrySelector
