import { useState, useEffect} from 'react';
function useStats(){
    const[stats, setStats] = useState(url);
    useEffect(() => {
        async function fetchData() {
            console.log('Fetching Data');
            const data = await fetch(url).then
            (res => res.json());
            setStats(data);
        }
        fetchData();       
    }, []);
    return stats;
}

export default useStats;
